import React, { useState } from "react";

// Komponen untuk Register
const Register = ({ onRegister, switchToLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleRegister = () => {
    // Lakukan validasi atau proses register disini
    // Contoh: Simpan data ke database atau state aplikasi
    onRegister(username, password);
  };

  return (
    <div>
      <h2>Register</h2>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleRegister}>Register</button>
      <p>
        Sudah memiliki akun? <button onClick={switchToLogin}>Login</button>
      </p>
    </div>
  );
};

// Komponen untuk Login
const Login = ({ onLogin, switchToRegister }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = () => {
    // Lakukan validasi atau proses login disini
    // Contoh: Verifikasi data di database atau state aplikasi
    onLogin(username, password);
  };

  return (
    <div>
      <h2>Login</h2>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Login</button>
      <p>
        Belum memiliki akun?{" "}
        <button onClick={switchToRegister}>Register</button>
      </p>
    </div>
  );
};

// Komponen untuk Chat
const ChatApp = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isRegisterMode, setIsRegisterMode] = useState(false);

  const handleRegister = (username, password) => {
    // Proses register, misalnya menyimpan data user ke database
    console.log("Registered:", username);
    setIsRegisterMode(false); // Setelah register, kembali ke mode login
  };

  const handleLogin = (username, password) => {
    // Lakukan verifikasi login, misalnya dengan memeriksa data di database
    // Untuk contoh sederhana, cukup set isLoggedIn menjadi true
    setIsLoggedIn(true);
  };

  const switchToRegister = () => {
    setIsRegisterMode(true);
  };

  const switchToLogin = () => {
    setIsRegisterMode(false);
  };

  return (
    <div>
      {!isLoggedIn ? (
        <>
          {isRegisterMode ? (
            <Register
              onRegister={handleRegister}
              switchToLogin={switchToLogin}
            />
          ) : (
            <Login onLogin={handleLogin} switchToRegister={switchToRegister} />
          )}
        </>
      ) : (
        <h2>Welcome to Chat Application</h2>
        // Komponen chat akan dimasukkan disini setelah login berhasil
      )}
    </div>
  );
};

export default ChatApp;
