import React, { useState } from "react";

const Message = ({ message, onEdit, onDelete }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editedMessage, setEditedMessage] = useState(message.content);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSaveEdit = () => {
    onEdit(message.id, editedMessage);
    setIsEditing(false);
  };

  const handleDelete = () => {
    onDelete(message.id);
  };

  const handleChange = (e) => {
    setEditedMessage(e.target.value);
  };

  return (
    <div>
      <p>From: {message.from}</p>
      <p>To: {message.to}</p>
      {isEditing ? (
        <textarea value={editedMessage} onChange={handleChange}></textarea>
      ) : (
        <p>{message.content}</p>
      )}
      {isEditing ? (
        <button onClick={handleSaveEdit}>Save</button>
      ) : (
        <>
          <button onClick={handleEdit}>Edit</button>
          <button onClick={handleDelete}>Delete</button>
        </>
      )}
    </div>
  );
};

const ChatApp = ({ username }) => {
  const [message, setMessage] = useState("");
  const [toUser, setToUser] = useState("");
  const [sentMessages, setSentMessages] = useState([]);

  const handleMessageChange = (e) => {
    setMessage(e.target.value);
  };

  const handleToUserChange = (e) => {
    setToUser(e.target.value);
  };

  const sendMessage = () => {
    if (message.trim() !== "") {
      const newMessage = {
        id: Date.now(),
        from: username,
        to: toUser,
        content: message,
      };
      setSentMessages([...sentMessages, newMessage]);
      setMessage("");
      setToUser("");
    }
  };

  const handleEditMessage = (id, editedContent) => {
    const updatedMessages = sentMessages.map((msg) =>
      msg.id === id ? { ...msg, content: editedContent } : msg
    );
    setSentMessages(updatedMessages);
  };

  const handleDeleteMessage = (id) => {
    const updatedMessages = sentMessages.filter((msg) => msg.id !== id);
    setSentMessages(updatedMessages);
  };

  return (
    <div>
      <h2>Message App</h2>
      <input
        type="text"
        placeholder="To"
        value={toUser}
        onChange={handleToUserChange}
      />
      <textarea
        rows="4"
        cols="50"
        value={message}
        onChange={handleMessageChange}
        placeholder="Type your message..."
      />
      <button onClick={sendMessage}>Send</button>
      <div>
        {sentMessages.map((msg) => (
          <Message
            key={msg.id}
            message={msg}
            onEdit={handleEditMessage}
            onDelete={handleDeleteMessage}
          />
        ))}
      </div>
    </div>
  );
};

export default ChatApp;
