import React, { useState } from "react";
import "./App.css";

// Komponen untuk Register
const Register = ({ onRegister, switchToLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleRegister = () => {
    // Lakukan validasi atau proses register disini
    // Contoh: Simpan data ke database atau state aplikasi
    onRegister(username, password);
  };

  return (
    <div>
      <h2>Register</h2>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleRegister}>Register</button>
      <p>
        Sudah memiliki akun? <button onClick={switchToLogin}>Login</button>
      </p>
    </div>
  );
};

// Komponen untuk Login
const Login = ({ onLogin, switchToRegister }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = () => {
    // Lakukan validasi atau proses login disini
    // Contoh: Verifikasi data di database atau state aplikasi
    onLogin(username, password);
  };

  return (
    <div>
      <h2>Login</h2>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Login</button>
      <p>
        Belum memiliki akun?{" "}
        <button onClick={switchToRegister}>Register</button>
      </p>
    </div>
  );
};

// Komponen untuk Chat
const ChatApp = ({ username }) => {
  const [message, setMessage] = useState("");
  const [isMouseOver, setIsMouseOver] = useState(false);
  const [keyPressed, setKeyPressed] = useState("");
  const [isLoaded, setIsLoaded] = useState(false);
  const [sentMessages, setSentMessages] = useState([]);

  const handleMessageChange = (e) => {
    setMessage(e.target.value);
  };

  const handleMouseOver = () => {
    setIsMouseOver(true);
  };

  const handleMouseOut = () => {
    setIsMouseOver(false);
  };

  const handleKeyDown = (e) => {
    setKeyPressed(e.key);
  };

  const handleLoad = () => {
    setIsLoaded(true);
  };

  const sendMessage = () => {
    if (message.trim() !== "") {
      setSentMessages([...sentMessages, { from: username, content: message }]);
      setMessage("");
    }
  };

  return (
    <div>
      <h2>Message App</h2>
      <textarea
        rows="4"
        cols="50"
        value={message}
        onChange={handleMessageChange}
        placeholder="Type your message..."
      />
      <button onClick={sendMessage}>Send</button>
      <p onMouseOver={handleMouseOver} onMouseOut={handleMouseOut}>
        {isMouseOver
          ? "Mouse is over the message area!"
          : "Mouse is out of the message area!"}
      </p>
      <p>Pressed Key: {keyPressed ? keyPressed : "None"}</p>
      <div>
        {sentMessages.map((msg, index) => (
          <div key={index}>
            <p>From: {msg.from}</p>
            <p>{msg.content}</p>
          </div>
        ))}
      </div>
      <div>
        <img
          src="https://via.placeholder.com/150"
          alt="Placeholder"
          onLoad={handleLoad}
        />
        {isLoaded && <p>Image is loaded!</p>}
      </div>
    </div>
  );
};

// Komponen induk untuk mengatur alur aplikasi
const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isRegisterMode, setIsRegisterMode] = useState(false);
  const [username, setUsername] = useState("");

  const handleRegister = (username, password) => {
    // Proses register, misalnya menyimpan data user ke database
    console.log("Registered:", username);
    setUsername(username);
    setIsRegisterMode(false); // Setelah register, kembali ke mode login
  };

  const handleLogin = (username, password) => {
    // Lakukan verifikasi login, misalnya dengan memeriksa data di database
    // Untuk contoh sederhana, cukup set isLoggedIn menjadi true
    setUsername(username);
    setIsLoggedIn(true);
  };

  const switchToRegister = () => {
    setIsRegisterMode(true);
  };

  const switchToLogin = () => {
    setIsRegisterMode(false);
  };

  return (
    <div>
      {!isLoggedIn ? (
        <>
          {isRegisterMode ? (
            <Register
              onRegister={handleRegister}
              switchToLogin={switchToLogin}
            />
          ) : (
            <Login onLogin={handleLogin} switchToRegister={switchToRegister} />
          )}
        </>
      ) : (
        <ChatApp username={username} />
      )}
    </div>
  );
};

export default App;
